class Solution {
    // 计数器，用于记录生成的排列数量
    int count = 0;
    // 用于保存最终的结果
    StringBuilder res = new StringBuilder();

    public String getPermutation(int n, int k) {
        // 用于暂存当前排列的链表
        LinkedList<Integer> temp = new LinkedList<>();
        // 标记数字是否已被使用
        boolean[] unused = new boolean[n];
        // 调用回溯函数生成排列
        backTrace(1, n, k, temp, unused);
        return res.toString();
    }

    // 回溯函数生成排列
    private void backTrace(int x,
                           int n,
                           int k,
                           LinkedList<Integer> temp,
                           boolean[] unused) {
        // 如果已生成的排列长度为 n，则判断是否为第 k 个排列
        if (temp.size() == n) {
            count++;
            if (count == k) {
                // 如果是第 k 个排列，则将排列中的数字加入结果字符串
                temp.forEach(t -> res.append(t));
            }
            return;
        }

        // 遍历可用的数字
        for (int i = 1; i <= n; i++) {
            // 如果数字已被使用，则跳过当前循环
            if (unused[i - 1]) {
                continue;
            }
            // 将当前数字加入暂存排列中
            temp.addLast(i);
            unused[i - 1] = true;
            // 递归调用回溯函数，生成下一个位置的数字
            backTrace(i + 1, n, k, temp, unused);
            if (count == k) {
                // 如果已生成第 k 个排列，则跳出循环
                break;
            }
            // 回溯操作，将当前数字从暂存排列中移除
            temp.removeLast();
            unused[i - 1] = false;
        }
    }
}
