            temp.addLast(nums[i]);
            // 递归调用回溯函数，生成下一个位置的元素
            backTrace(nums, res, temp, i + 1, length, unused);
            // 回溯操作，将当前元素从暂存子集中移除
            unused[i] = false;
            temp.removeLast();
        }
    }
}
